mapper.select:
select count(id) from
(select rownum nun, n.* from (select * from v_clubList where ${field}
like '%'||#{search}||'%' order by id)n)

mapper.insert:
INSERT INTO tb_club (
id, name, name_eng, sports_id, league_id, city, stadium_name,
stadium_address, found_year, website, intro
) VALUES (
#{id}, #{name}, #{name_eng}, #{sports_id}, #{league_id}, #{city}, #{stadium_name},
#{stadium_address}, #{found_year}, #{website}, #{intro}
)
